                const readbackBuffer = device.createBuffer({
                    size: M * N * Float32Array.BYTES_PER_ELEMENT,
                    usage: GPUBufferUsage.COPY_DST | GPUBufferUsage.MAP_READ,
                });

                // Create compute shader
                const shaderModule = device.createShaderModule({
                    code: `
                        @group(0) @binding(0) var<storage, read> a : array<f32>;
                        @group(0) @binding(1) var<storage, read> b : array<f32>;
                        @group(0) @binding(2) var<storage, read_write> result : array<f32>;

                        @compute @workgroup_size(8, 8)
                        fn main(@builtin(global_invocation_id) global_id : vec3<u32>) {
                            let i = global_id.x;
                            let j = global_id.y;

                            if (i >= ${M}u || j >= ${N}u) {
                                return;
                            }

                            var sum = 0.0;
                            for (var k = 0u; k < ${K}u; k = k + 1u) {
                                sum = sum + a[i * ${K}u + k] * b[k * ${N}u + j];
                            }

                            result[i * ${N}u + j] = sum;
                        }
                    `
                });

                // Create pipeline
                const computePipeline = device.createComputePipeline({
                    layout: "auto",
                    compute: {
                        module: shaderModule,
                        entryPoint: "main"
                    }
                });

                // Create bind group
                const bindGroup = device.createBindGroup({
                    layout: computePipeline.getBindGroupLayout(0),
                    entries: [
                        { binding: 0, resource: { buffer: bufferA } },
                        { binding: 1, resource: { buffer: bufferB } },
                        { binding: 2, resource: { buffer: resultBuffer } }
                    ]
                });

                // Encode and submit commands
                const commandEncoder = device.createCommandEncoder();
                const passEncoder = commandEncoder.beginComputePass();
                passEncoder.setPipeline(computePipeline);
                passEncoder.setBindGroup(0, bindGroup);
                passEncoder.dispatchWorkgroups(Math.ceil(M / 8), Math.ceil(N / 8), 1);
                passEncoder.end();

                // Copy result to readback buffer
                commandEncoder.copyBufferToBuffer(
                    resultBuffer,
                    0,
                    readbackBuffer,
                    0,
                    M * N * Float32Array.BYTES_PER_ELEMENT
                );

                device.queue.submit([commandEncoder.finish()]);

                // Read back results
                await readbackBuffer.mapAsync(GPUMapMode.READ);
                const resultArray = new Float32Array(readbackBuffer.getMappedRange());
                const resultMatrix = [];
                for (let i = 0; i < M; i++) {
                    resultMatrix.push(Array.from(resultArray.slice(i * N, (i + 1) * N)));
                }
                readbackBuffer.unmap();

                status.textContent = `Computation completed! (${M}x${N} matrix)`;
                status.className = "status success";

                return resultMatrix;
            } catch (error) {
                status.textContent = "WebGPU Error: " + error.message;
                status.className = "status error";
                console.error(error);
                return null;
            }
        }

        // Update matrix size display
        function updateMatrixSizes() {
            const matrixAInput = document.getElementById('matrixA').value;
            const matrixBInput = document.getElementById('matrixB').value;

            try {
                const matrixA = parseMatrix(matrixAInput);
                const matrixB = parseMatrix(matrixBInput);

                document.getElementById('sizeA').textContent = 
                    matrixA.length > 0 ? `Size: ${matrixA.length}x${matrixA[0].length}` : "Size: -";
                document.getElementById('sizeB').textContent = 
                    matrixB.length > 0 ? `Size: ${matrixB.length}x${matrixB[0].length}` : "Size: -";
            } catch {
                document.getElementById('sizeA').textContent = "Size: -";
                document.getElementById('sizeB').textContent = "Size: -";
            }
        }

        // Main function to handle multiplication
        async function multiplyMatrices() {
            const matrixAInput = document.getElementById('matrixA').value;
            const matrixBInput = document.getElementById('matrixB').value;

            const matrixA = parseMatrix(matrixAInput);
            const matrixB = parseMatrix(matrixBInput);

            if (!validateMatrices(matrixA, matrixB)) return;

            const resultMatrix = await multiplyMatricesWebGPU(matrixA, matrixB);

            if (resultMatrix) {
                document.getElementById('result').textContent = formatMatrix(resultMatrix);
            }
        }

        // Reset matrices to example
        function resetMatrices() {
            document.getElementById('matrixA').value = "1,2,3\n4,5,6";
            document.getElementById('matrixB').value = "7,8\n9,10\n11,12";
            document.getElementById('result').textContent = "The result will appear here...";
            document.getElementById('status').textContent = "";
            document.getElementById('status').className = "status";
            updateMatrixSizes();
        }

        // Initialize the app
        async function init() {
            const isWebGPUSupported = await checkWebGPUSupport();

            if (isWebGPUSupported) {
                document.getElementById('multiplyBtn').addEventListener('click', multiplyMatrices);
                document.getElementById('resetBtn').addEventListener('click', resetMatrices);

                // Update sizes when matrices change
                document.getElementById('matrixA').addEventListener('input', updateMatrixSizes);
                document.getElementById('matrixB').addEventListener('input', updateMatrixSizes);

                // Initialize sizes
                updateMatrixSizes();
            } else {
                document.getElementById('multiplyBtn').disabled = true;
            }
        }

        // Start the application
        window.addEventListener('load', init);
    </script>
